#include "../../../../test/CuTest.h"

#include "UtilDecode.h"

#include <stdlib.h>


CuSuite* UtilDecodeGetTestSuite(void);

void bitPackingDecoding_test(CuTest *tc);


CuSuite* UtilDecodeGetTestSuite()
{
    CuSuite *suite = CuSuiteNew();
    SUITE_ADD_TEST(suite, bitPackingDecoding_test);
    return suite;
}

void bitPackingDecoding_test(CuTest *tc)
{
#define ARRAY_SIZE 5
    /* The ZERO value after the END HERE comment is the terminal character of every string, and it cannot be taken in account when packing */
    char input1[ARRAY_SIZE][56] = { {0} };
    const char input2[ARRAY_SIZE][56] = {
        { 0x10, 0x0E, 0x09, 0x10, 0x03, 0x00, 0x02, 0x04, 0x01, 0x15, 0x04, 0x00, 0x18, 0x00, 0x1C, 0x1E, 0x1F, 0x07, 0x10, 0x00, 0x00, 0x00, 0x00, 0x00 /* END HERE */, 0x00 }, /* 1?J9N/X?4P?34??764?0P??W (WM - VALID) */
        { 0x10, 0x0E, 0x09, 0x10, 0x03, 0x00, 0x02, 0x04, 0x01, 0x15, 0x04, 0x00, 0x19, 0x00, 0x1C, 0x1E, 0x1F, 0x07, 0x10, 0x00, 0x00, 0x00, 0x00, 0x00 /* END HERE */, 0x00 }, /* 2?J9N/X?4P?34??764?0P??W (WM - INVALID) */
        { 0x15, 0x0A, 0x09, 0x10, 0x02, 0x1B, 0x00, 0x15, 0x17, 0x01, 0x0A, 0x00, 0x10, 0x07, 0x05, 0x12, 0x00, 0x08, 0x00, 0x03, 0x00, 0x00, 0x00, 0x00 /* END HERE */, 0x00 }, /* 4?6F7M+?4JNRJ*??K??0+9?? (WM - VALID) */
        { 0x19, 0x09, 0x14, 0x13, 0x00, 0x16, 0x13, 0x03, 0x01, 0x05, 0x18, 0x1C, 0x08, 0x13, 0x04, 0x10, 0x12, 0x1C, 0x19, 0x14, 0x1A, 0x09, 0x0F, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x01, 0x1D, 0x0B, 0x12, 0x01, 0x00, 0x15, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x50, 0x00 /* END HERE */, 0x00 }, /* ?M???.R066???2FC?!?R????3HCP?-??32H???Y?M4C??1J??NQ04? (SOS - VALID) */
        { 0x03, 0x08, 0x18, 0x14, 0x00, 0x06, 0x05, 0x02, 0x00, 0x13, 0x17, 0x14, 0x1F, 0x1A, 0x07, 0x15, 0x01, 0x17, 0x02, 0x04, 0x11, 0x15, 0x0C, 0x0C, 0x03, 0x08, 0x04, 0x15, 0x17, 0x09, 0x0C, 0x05, 0x03, 0x1B, 0x00, 0x00, 0x00, 0x00, 0x00, 0x01, 0x1D, 0x0B, 0x12, 0x01, 0x00, 0x15, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x05, 0x00 /* END HERE */, 0x00 }  /* S6???.RF?6F??NWH*5KC???RH1!9?8?JK7P0??SNMJRPSKJ??7QJ4N (SOS - VALID) */
    };
    int input3[ARRAY_SIZE] = { 24, 24, 24, 55, 55 };

    const char expected[ARRAY_SIZE][35] = {
        { 0xD0, 0x25, 0x38, 0x80, 0x20, 0xA1, 0x12, 0x80, 0x01, 0xF7, 0xFF, 0x40, 0x00, 0x00, 0x00 /* END HERE */, 0x00 },
        { 0xD0, 0x25, 0x38, 0x80, 0x20, 0xA1, 0x12, 0x90, 0x01, 0xF7, 0xFF, 0x40, 0x00, 0x00, 0x00 /* END HERE */, 0x00 },
        { 0x55, 0x25, 0x28, 0x36, 0xA8, 0x37, 0x28, 0x00, 0x4F, 0x91, 0x00, 0x81, 0x01, 0x00, 0x00 /* END HERE */, 0x00 },
        { 0x39, 0xD1, 0x09, 0xEC, 0x1C, 0xA1, 0x60, 0x8E, 0x26, 0x81, 0x92, 0x67, 0xAA, 0xD3, 0x03, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x00, 0x08, 0x7D, 0x49, 0x58, 0x01, 0x00, 0x00, 0x00, 0x50, 0x00 /* END HERE */, 0x00 },
        { 0x03, 0x61, 0x0A, 0x4C, 0x11, 0x60, 0x5E, 0xFA, 0xF5, 0xA9, 0xE1, 0x0A, 0x12, 0x2B, 0x63, 0x03, 0x91, 0x7A, 0x13, 0x2B, 0x63, 0x03, 0x00, 0x00, 0x08, 0x7D, 0x49, 0x58, 0x01, 0x00, 0x00, 0x00, 0x50, 0x00 /* END HERE */, 0x00 }
    };

    int i, j;
    for (i = 0; i < ARRAY_SIZE; ++i) {
        bitPackingDecoding(input1[i], input2[i], input3[i]);
        for (j = 0; j < input3[i]; ++j) {
            CuAssertStrEquals(tc, expected[i], input1[i]);
        }
    }
#undef ARRAY_SIZE
}
